
public class NumbersSwap {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n1 = 80;
		int n2 = 90;
		
		System.out.println("First Number: " + n1);
		System.out.println("Second Number: " + n2);
		
		System.out.println("\nAfter Swapping\n");
		
		int n3;
		n3 = n1;
		n1 = n2;
		n2 = n3;
		
		System.out.println("First Number: " + n1);
		System.out.println("Second Number: " + n2);

	}

}
